    if(list[i]==uName || list1[i]==gName || list2[i]==fName || list3[i]==accountActive || list4[i]==email || list5[i]==tel || list5[i]==mobile || list6[i]==id){
        log.info "Input query filter is found"
        break
    }
    else{
        log.error "input query filter not found"
